Script.script:
inns = inns or {}
local profilePath = getMudletHomeDir() profilePath = profilePath:gsub("\\","/")
t2tvars.innTable = t2tvars.innTable or {}
function t2tMapper.addInn(inn) if not table.contains(t2tvars.innTable,inn) then t2tvars.innTable[inn] = false cecho(f"\n<red>{inn} <cyan>added to table.\n") else cecho(f"\n<red>{inn} <cyan>already in list.\n") end t2tvars.save() end
function resetInns() for inn, value in pairs(t2tvars.innTable) do t2tvars.innTable[inn] = false end t2tvars.save() end
function createRentedInnList() local rentedRooms = {} for inn, status in pairs(t2tvars.innTable) do if t2tvars.innTable[inn] == true then table.insert(rentedRooms,inn) end end t2tvars["Rented"] = table.concat(rentedRooms, ", ") t2tvars.save() end
function inns.eventHandler(event,...) if event == "onInnReset" then resetInns() elseif event == "onUpdateInns" then createRentedInnList() end end
inns.registeredEvents = { registerAnonymousEventHandler("onInnReset","inns.eventHandler"), registerAnonymousEventHandler("onUpdateInns","inns.eventHandler"), }

mordor = mordor or {}
local profilePath = getMudletHomeDir() profilePath = profilePath:gsub("\\","/")
local function setMordorUptime(pass) mordorPassStopWatch = mordorPassStopWatch or createStopWatch("mordorPassStopWatch",true) resetStopWatch("mordorPassStopWatch") startStopWatch("mordorPassStopWatch") setStopWatchPersistence("mordorPassStopWatch", true) t2tvars["mordorPass"] = pass t2tvars.save() end
local function getMordorTime() mordorTimeTable = getStopWatchBrokenDownTime("mordorPassStopWatch") local mordorTimeDays = mordorTimeTable["days"] local mordorTimeHours = mordorTimeTable["hours"] local mordorTimeMins = mordorTimeTable["minutes"] local mordorTimeSecs = mordorTimeTable["seconds"] t2tvars['mordorTimeString'] = f"{mordorTimeDays}d {mordorTimeHours}h {mordorTimeMins}m {mordorTimeSecs}s" t2tvars.save() end
function mordor.eventHandler(event,...) if event == 'onSetMordorUptime' then setMordorUptime(arg[1]) elseif event == 'onGetMordorTime' then getMordorTime() end end
mordor.registeredEvents = { registerAnonymousEventHandler("onSetMordorUptime", "mordor.eventHandler"), registerAnonymousEventHandler("onGetMordorTime", "mordor.eventHandler"), }

local profilePath = getMudletHomeDir() profilePath = profilePath:gsub("\","/")
trainers = trainers or {}
if io.exists(profilePath.."/trainers/trainers.lua") then table.load(profilePath.."/trainers/trainers.lua", trainers) end
function saveTrainers() table.save(profilePath.."/trainers/trainers.lua", trainers) end
trainers.archer = trainers.archer or {} trainers.assassin = trainers.assassin or {} trainers.civilian = trainers.civilian or {} trainers.language = trainers.language or {} trainers.minion = trainers.minion or {} trainers.necromancer = trainers.necromancer or {} trainers.ranger = trainers.ranger or {} trainers.rider = trainers.rider or {} trainers.sniffer = trainers.sniffer or {} trainers.thief = trainers.thief or {} trainers.tradeskill = trainers.tradeskill or {} trainers.warrior = trainers.warrior or {} trainers.wizard = trainers.wizard or {}
function findTrainer(query) local queryTable local skillsMatch = {} local skillsMatchList = {} local nameMatch = {} for search, profs in pairs(trainers) do for _,prof in ipairs(profs) do local skillList skillsMatch[prof.name] = prof.skills skillsMatchList[prof.name] = string.split(skillsMatch[prof.name],", ") end end if table.contains(table.keys(trainers),query) then local profTrainers = trainers[query] for search, trainer in ipairs(profTrainers) do cecho(f"<cyan>Trainer: <red>{trainer.name}, <cyan>Location: <red>{trainer.area}
") end elseif table.contains(skillsMatchList, query) and not table.contains(table.keys(skillsMatchList),query) then for trainer,skills in pairs(skillsMatchList) do for _,skill in ipairs(skills) do if skill == query then nameMatch[trainer] = true end end end cecho(f"<cyan>These trainers train {query}:
") for trainer, boo in pairs(nameMatch) do for search,profs in pairs(trainers) do for _,prof in ipairs(profs) do if prof.name == trainer then cecho(f"<cyan>Trainer: <red>{prof.name}, <cyan>Location: <red>{prof.area}
") end end end end elseif table.contains(trainers,query) then for search,profs in pairs(trainers) do for _,prof in ipairs(profs) do if prof.name == query then cecho(f"<cyan>Trainer: <red>{prof.name}, <cyan>Location: <red>{prof.area}, <cyan>Skills: <red>{prof.skills}, <cyan>Affinity: <red>{prof.affinity}
") elseif prof.area == query then cecho(f"<cyan>Trainer: <red>{prof.name}, <cyan>Location: <red>{prof.area}, <cyan>Skills: <red>{prof.skills}, <cyan>Affinity: <red>{prof.affinity}
") end end end else cecho("<red>No results found
") end end
